import React,{useState} from 'react';
import Header from './Header';
import Footer from './Footer';
import Note from './Note';
import CreateArea from './CreateArea';
function App(){
    const [character,setCharacter]=useState([
      {
          id:1,
          charac: "Megala",
          mydesp: "Gentle,Naughty"
      },
      {
          id:2,
          charac: "Sushmi",
          mydesp: "Mature,Extrovert"
      },
      {
          id:3,
          charac: "Dimple",
          mydesp: "Sensitive,Loving"
      }
  ]);
    return <div>
    <Header />
    <CreateArea />
    <div class="notes-container">
    {character.map((inf) => (
          <Note
            key={inf.id }
            title={inf.charac}
            des={inf.mydesp}
          />
        ))}
</div>
    <Footer />
    </div>;
}
export default App;
